import React from "react";

import {
	ExploreContainer,
	ExploreTitle,
	ExploreSections,
} from "./Explore.styles";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectDirectorySections } from "../../redux/directory/directorySelectors";
import MenuItem from "../directory/Directory";

const Explore = ({ sections, titleToFilter }) => {
	console.log(sections);
	console.log(titleToFilter);
	return (
		<ExploreContainer>
			<ExploreTitle>Explore More</ExploreTitle>
			<ExploreSections>
				{sections
					.filter((section) => section.title != titleToFilter.toUpperCase())
					.map(({ id, ...sectionProps }) => {
						return <MenuItem key={id} {...sectionProps} />;
					})}
			</ExploreSections>
		</ExploreContainer>
	);
};

const mapStateToProps = createStructuredSelector({
	sections: selectDirectorySections,
});

export default connect(mapStateToProps)(Explore);
