import React, { lazy, Suspense, useEffect } from "react";
import {
	BrowserRouter as Router,
	Route,
	Switch,
	Redirect,
} from "react-router-dom";
import Navbar from "./Components/navbar/Navbar";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCurrentUser } from "./redux/user/userSelectors";
import { checkUserSession } from "./actions/userActions";
import { GlobalStyle } from "./global.styles";
import {
	data,
	addCollectionAndDocuments,
	convertCollectionsSnapshotToMap,
	addDataToCollections,
} from "./firebase/firebase";
import SHOP_DATA from "./redux/shop/shopData";

// <-- Components -->
import Spinner from "./Components/spinner/Spinner";
// <-- Views -->
const HomePage = lazy(() => import("./views/homepage/Homepage"));
const ShopPage = lazy(() => import("./views/shopPage/ShopPage"));
const SignInAndSignUp = lazy(() =>
	import("./views/sign-in-and-sign-up/SignInAndSignUp")
);
const CheckOut = lazy(() => import("./views/checkout/CheckOut"));
const SingleProductPage = lazy(() =>
	import("./views/SingleProductPage/SingleProductPage")
);

const App = ({ currentUser, checkUserSession }) => {
	useEffect(() => {
		checkUserSession();
		console.log(data);
		const dat = SHOP_DATA;
		//const itemsMap = convertCollectionsSnapshotToMap(data);
		addCollectionAndDocuments(
			"collections",
			SHOP_DATA.map(({ title, items }) => ({ title, items }))
		);
		//console.log(itemsMap);
	}, [checkUserSession]);

	return (
		<>
			<Router>
				<GlobalStyle />
				<Navbar />
				<Switch>
					<Suspense fallback={<Spinner />}>
						<Route exact path="/" component={HomePage} />

						<Route path="/shop" component={ShopPage} />
						<Route exact path="/checkout" component={CheckOut} />
						<Route path="/product/:itemId" component={SingleProductPage} />
						<Route
							exact
							path="/signIn"
							render={() =>
								currentUser ? <Redirect to="/" /> : <SignInAndSignUp />
							}
						/>
					</Suspense>
				</Switch>
			</Router>
		</>
	);
};

const mapStateToProps = createStructuredSelector({
	currentUser: selectCurrentUser,
});

const mapDispatchToProps = (dispatch) => ({
	checkUserSession: () => dispatch(checkUserSession()),
});

export default connect(mapStateToProps, mapDispatchToProps)(App);
