import { put, takeLatest, all, call } from "redux-saga/effects";

import UserActionTypes from "./userTypes";

import {
	auth,
	googleProvider,
	createUserProfileDocument,
} from "../../firebase/firebase";
import {
	googleSignInSuccess,
	googleSignInFailure,
} from "../../actions/userActions";

export function* googleSignInAsync() {
	try {
		const { user } = yield auth.signInWithPopup(googleProvider);
		const userRef = yield call(createUserProfileDocument, user);
		const userSnapshot = yield userRef.get();
		yield put(
			googleSignInSuccess({ id: userSnapshot.id, ...userSnapshot.data() })
		);
	} catch (error) {
		yield put(googleSignInFailure(error.message));
	}
}
export function* signInWithEmailAsync() {
	try {
	} catch (error) {}
}

export function* googleSignInStart() {
	yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignInAsync);
}

export function* userSagas() {
	yield all([call(googleSignInStart)]);
}
