import React, { useEffect } from "react";
import { Route } from "react-router-dom";
import SingleProduct from "../../Components/singleProduct/SingleProduct";
import { getSingleProductStart } from "../../actions/productActions";
import { fetchCollectionsStart } from "../../actions/shopActions";
import { connect } from "react-redux";

const SingleProductPage = ({
	getSingleProductStart,
	match,
	fetchCollectionsStart,
}) => {
	const { itemId } = match.params;
	useEffect(() => {
		fetchCollectionsStart();

		//getSingleProductStart(itemId);
	}, []);

	return (
		<>
			<h1>Single Product Page</h1>
			<Route
				path={`/product/${itemId}`}
				render={(props) => <SingleProduct {...props} />}
			/>
		</>
	);
};

const mapDispatchToProps = (dispatch) => ({
	getSingleProductStart: (itemId) => dispatch(getSingleProductStart(itemId)),
	fetchCollectionsStart: () => dispatch(fetchCollectionsStart()),
});

export default connect(null, mapDispatchToProps)(SingleProductPage);
