import React, { useState } from "react";

import { CarouselContainer, CarouselData } from "./Carousel.styles";
import RightArrow from "./arrows/RightArrow";
import LeftArrow from "./arrows/LeftArrow";

const INITIAL_VALUES = {
	INITIAL_FIRST_INDEX: 0,
	INITIAL_LAST_INDEX: 3,
	INTERVAL: 4,
};

const Carousel = ({ data, children }) => {
	const [firstIndex, setFirstIndex] = useState(
		INITIAL_VALUES.INITIAL_FIRST_INDEX
	);
	const [lastIndex, setLastIndex] = useState(INITIAL_VALUES.INITIAL_LAST_INDEX);
	const interval = INITIAL_VALUES.INTERVAL;
	const goToNext = () => {
		if (lastIndex + interval > data.length) {
			setFirstIndex(data.length - interval);
			setLastIndex(data.length);
		} else {
			setFirstIndex(firstIndex + interval);
			setLastIndex(lastIndex + interval);
		}
	};

	const goToPrev = () => {
		if (firstIndex - interval > INITIAL_VALUES.INITIAL_FIRST_INDEX) {
			setFirstIndex(INITIAL_VALUES.INITIAL_FIRST_INDEX);
			setLastIndex(INITIAL_VALUES.INITIAL_FIRST_INDEX + interval);
		} else {
			setFirstIndex(firstIndex - interval);
			setLastIndex(lastIndex - interval);
		}
	};

	return (
		<CarouselContainer>
			<LeftArrow goToPrev={goToPrev} />
			<CarouselData>{children}</CarouselData>
			<RightArrow goToNext={goToNext} />
		</CarouselContainer>
	);
};

export default Carousel;
