import styled, { css } from "styled-components";

const arrowStyles = css`
	cursor: pointer;
	border: none;
	height: 10%;
	align-self: center;
	margin: 0 5px;
	background-color: lightgrey;
	opacity: 0.7;
`;

export const RightArrowContainer = styled.button`
	${arrowStyles}
`;

export const LeftArrowContainer = styled.button`
	${arrowStyles}
`;
