import styled from "styled-components";

export const DirectoryMenu = styled.div`
	width: 80%;
	display: flex;
	flex-wrap: wrap;
	justify-content: space-between;
	margin-top: 40px;

	@media screen and (max-width: 800px) {
		margin: 150px auto;
	}
`;
