import React, { useEffect } from "react";
import { Route } from "react-router-dom";
import SingleProduct from "../../Components/singleProduct/SingleProduct";
import { getSingleProductStart } from "../../actions/productActions";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionsForPreview } from "../../redux/shop/shopSelectors";
const SingleProductPage = ({ match, getSingleProductStart, collections }) => {
	const { itemId } = match.params;

	useEffect(() => {
		console.log(collections);
		const collectionsItems = collections.map((collection) => collection.items);
		const collectionItems = collectionsItems.map((items) => items);
		console.log("collectionsItems : ", collectionsItems[0]);
		//console.log("item", item);
		getSingleProductStart(itemId);
	}, []);
	return (
		<>
			<h1>Single Product Page</h1>
			<Route path={`/product/${itemId}`} component={SingleProduct} />
		</>
	);
};

const mapStateToProps = createStructuredSelector({
	collections: selectCollectionsForPreview,
});

const mapDispatchToProps = (dispatch) => ({
	getSingleProductStart: (itemId) => dispatch(getSingleProductStart(itemId)),
});

export default connect(mapStateToProps, mapDispatchToProps)(SingleProductPage);
