import SHOP_DATA from "./shopData";

const INITIAL_STATE = {
	shopData: SHOP_DATA,
};

const shopReducer = (state = INITIAL_STATE, action) => {
	const { type } = action;
	switch (type) {
		default:
			return state;
	}
};

export default shopReducer;
