import { takeLatest, all, call, put } from "redux-saga/effects";
import ProductActionTypes from "./productTypes";

import {
	searchProductSuccess,
	searchProductFailure,
} from "../../actions/productActions";

import { fetchCollectionsSuccess } from "../../actions/shopActions";
import { getResultItemsFromSearch } from "../../firebase/firebase";
import {
	firestore,
	convertCollectionsSnapshotToMap,
} from "../../firebase/firebase";

export function* searchProductsAsync({ payload: { search } }) {
	try {
		const collectionRef = firestore.collection("collections");
		const snapshot = yield collectionRef.get();
		const collectionsMap = yield call(
			convertCollectionsSnapshotToMap,
			snapshot
		);

		const mappedCollectionsItems = Object.keys(collectionsMap).map(
			(key) => collectionsMap[key].items
		);
		console.log(mappedCollectionsItems);
		const resultItems = mappedCollectionsItems.forEach((items) =>
			items.map((item) => item.filter((item) => item.name == search))
		);
		console.log(resultItems);
		//yield put(searchProductSuccess(resultItems));
	} catch (error) {
		//yield put(searchProductFailure(error.message));
	}
}

export function* onSearchProductsStart() {
	yield takeLatest(
		ProductActionTypes.SEARCH_PRODUCTS_START,
		searchProductsAsync
	);
}

export function* getSingleProductStart() {
	yield takeLatest(
		ProductActionTypes.GET_SINGLE_PRODUCT_START
		//getSingleProductAsync
	);
}

export function* productSagas() {
	yield all([call(onSearchProductsStart)]);
}
