import React, { useState } from "react";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import { addItem } from "../../actions/cartActions";
import {
	CollectionItemContainer,
	AddButton,
	PriceContainer,
	BackgroundImage,
	NameContainer,
	CollectionFooterContainer,
} from "./CollectionItem.styles";

const collectionItem = ({ item, addItem, history }) => {
	const { name, price, imageUrl } = item;

	const [added, setAdded] = useState(false);

	const handleAdded = () => {
		setAdded(true);
		setTimeout(() => setAdded(false), 500);
	};

	return (
		<CollectionItemContainer>
			<BackgroundImage
				imageurl={imageUrl}
				onClick={() => {
					history.push(`/product/${item.id}`);
				}}
			/>
			<div className={`${added ? "added" : ""} addedShow fast-fade-in`}>
				<span>Added!</span>
			</div>

			<CollectionFooterContainer>
				<NameContainer>{name}</NameContainer>
				<PriceContainer className="price">{`R$${price}`}</PriceContainer>
			</CollectionFooterContainer>
			<AddButton onClick={(() => addItem(item), handleAdded())} inverted>
				Add to Cart
			</AddButton>
		</CollectionItemContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	addItem: (item) => dispatch(addItem(item)),
});

export default connect(null, mapDispatchToProps)(withRouter(collectionItem));
