import styled from "styled-components";

export const SingleProductPageContainer = styled.div`
	margin: auto;
	padding: 30px 10px;
	width: 85vw;
	height: 100vh;
	display: flex;
	flex-flow: row wrap;
	justify-content: flex-start;
	align-items: flex-start;
`;
