import styled from "styled-components";

export const RecentlyContainer = styled.div`
	width: 90%;
	display: flex;
	align-items: left;

	@media screen and (max-width: 800px) {
		margin: 50px auto;
	}
`;

export const RecentlyTitle = styled.h2``;
