import { takeLatest, all, call, put } from "redux-saga/effects";
import ProductActionTypes from "./productTypes";
import {
	getSingleProductSuccess,
	getSingleProductFailure,
} from "../../actions/productActions";
import { fetchCollectionsStart } from "../../actions/shopActions";
import ShopActionTypes from "../shop/shopTypes";

export function* getSingleProductAsync(id) {
	try {
		const collections = null;
		console.log("collections: ", collections);
		console.log("id : ", id);

		//yield put(getSingleProductSuccess(product))
	} catch (error) {
		//yield put(getSingleProductFailure(error.message))
	}
}

export function* getSingleProductStart() {
	yield takeLatest(
		ProductActionTypes.GET_SINGLE_PRODUCT_START,
		getSingleProductAsync
	);
}

export function* productSagas() {
	yield all([call(getSingleProductStart)]);
}
