import React from "react";
import { withRouter } from "react-router-dom";
import {
	DescriptionCointainer,
	DescriptionDetails,
	DescriptionButtonsContainer,
	Description,
	AddButton,
	CheckOutButton,
} from "./DescriptionProduct.styles";
import { addItem } from "../../actions/cartActions";

import { connect } from "react-redux";

const DescriptionProduct = ({ addItem, product, history }) => (
	<DescriptionCointainer>
		<DescriptionDetails>
			<h3>
				<strong>Descrição</strong>
			</h3>
			<Description>
				Lorem Ipsum é simplesmente uma simulação de texto da indústria
				tipográfica e de impressos, e vem sendo utilizado desde o século XVI,
				quando um impressor desconhecido pegou uma bandeja de tipos e os
				embaralhou para fazer um livro de modelos de tipos. Lorem Ipsum
				sobreviveu não só a cinco séculos, como também ao salto para a
				editoração eletrônica, permanecendo essencialmente inalterado. Se
				popularizou na década de 60, quando a Letraset lançou decalques contendo
				passagens de Lorem Ipsum, e mais recentemente quando passou a ser
				integrado a softwares de editoração eletrônica como Aldus PageMaker.
			</Description>
			<DescriptionButtonsContainer>
				<AddButton onClick={() => addItem(product)}>Add to Cart</AddButton>
				<CheckOutButton
					onClick={() => {
						history.push("/checkout");
					}}
				>
					Checkout
				</CheckOutButton>
			</DescriptionButtonsContainer>
		</DescriptionDetails>
	</DescriptionCointainer>
);

const mapDispatchToProps = (dispatch) => ({
	addItem: (product) => dispatch(addItem(product)),
});

export default connect(
	null,
	mapDispatchToProps
)(withRouter(DescriptionProduct));
