import styled from "styled-components";

export const SingleProductPageContainer = styled.div`
	margin: 20px 10px;
	padding: 30px 0;
	width: 100%;
	height: 150vh;
	display: flex;
	flex-flow: row wrap;
	justify-content: flex-start;
	align-items: flex-start;
`;
