import styled from "styled-components";

export const SingleProductPageContainer = styled.div`
	margin: auto;
	padding: 50px 20px;
	border: solid 10px red;
	width: 75vw;
	height: 90vh;
	display: flex;
	background-color: lightgreen;
	flex-flow: row wrap;
	justify-content: space-between;
	align-items: flex-start;
`;
