import { call, put, takeLatest } from "redux-saga/effects";

import { UserActionTypes } from "./userTypes";

import { firestore, signInWithGoogle } from "../../firebase/firebase";
import {
	googleSignInSuccess,
	googleSignInFailure,
} from "../../actions/userActions";

export function* googleSignInAsync() {
	try {
		const user = yield call(signInWithGoogle);
		console.log("user", user);

		yield put(googleSignInSuccess(user));
	} catch (error) {
		yield put(googleSignInFailure(error.message));
	}
}

export function* googleSignInStart() {
	yield takeLatest(UserActionTypes.GOOGLE_SIGN_IN_START, googleSignInAsync);
}
