import React from "react";
import { getSingleObject } from "../../firebase/firebase";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionsForPreview } from "../../redux/shop/shopSelectors";
import { BackgroundImage } from "../collection-item/CollectionItem.styles";
import { SingleProductContainer } from "./SingleProduct.styles";
const SingleProduct = ({ match, collections }) => {
	const itemId = match.path.slice(9, 11);
	console.log(collections);
	//console.log(typeof match.path);

	const product = getSingleObject(collections, itemId);
	const { id, price, imageUrl } = product;

	console.log(product);
	return (
		<SingleProductContainer>
			<BackgroundImage imageurl={imageUrl} />
		</SingleProductContainer>
	);
};

const mapStateToProps = createStructuredSelector({
	collections: selectCollectionsForPreview,
});

export default connect(mapStateToProps)(SingleProduct);
