import React from "react";

import CollectionItem from "../../Components/collection-item/CollectionItem";
import { selectShopCollections } from "../../redux/shop/shopSelectors";
import { createStructuredSelector } from "reselect";
import { connect } from "react-redux";

const Collection = ({ match, collections }) => {
	return (
		<div className="collection-page ">
			<h2 className="title">{match.params.collectionId.toUpperCase()}</h2>
			<div className="items">
				<div className="collection-items">
					{collections
						.filter((collection) => collection.id === match.params.collectionId)
						.map((collectionItem) => (
							<CollectionItem key={collectionItem.id} item={collectionItem} />
						))}
				</div>
			</div>
		</div>
	);
};

const mapStateToProps = createStructuredSelector({
	collections: selectShopCollections,
});

export default Collection;
