import React from "react";
import { useState } from "react";
import { withRouter } from "react-router-dom";
import { connect } from "react-redux";
import {
	SearchBarContainer,
	SearchBarInput,
	SearchButton,
} from "./SearchBar.styles";
import { searchProductsStart } from "../../actions/productActions";

const SearchBar = ({ searchProductsStart, history }) => {
	const [search, setSearch] = useState("");

	return (
		<SearchBarContainer>
			<SearchBarInput
				placeholder={"Search"}
				value={search}
				onChange={(e) => setSearch(e.target.value)}
			/>
			<SearchButton onClick={() => history.push(`/search/${search}`)}>
				<i className="fas fa-search"></i>
			</SearchButton>
		</SearchBarContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	searchProductsStart: (search) => dispatch(searchProductsStart(search)),
});

export default connect(null, mapDispatchToProps)(withRouter(SearchBar));
