import styled from "styled-components";

export const HomepageContainer = styled.div`
	display: flex;
	flex-direction: column;
	align-items: center;
	margin: 40px 20px;
`;

export const SectionsTitle = styled.h1`
	margin: auto;
	margin-top: 80px;
	font-size: 40px;

	transition: transform 0.5s;
	&:hover {
		transform: scale(1.6);
	}
`;
