import styled, { css } from "styled-components";

const mainColor = "black";

const shrinkLabel = css`
	top: -14px;
	font-size: 12px;
	color: ${mainColor};
`;

export const SearchBarContainer = styled.div`
	i {
		opacity: 0.4;

		margin-right: 30px;
	}

	&:focus ~ label {
		${shrinkLabel};
	}
`;

export const SearchBarInput = styled.input`
	width: 15rem;
	background: lightgrey;
	border: none;
	border-bottom: 1px solid grey;
	padding: 0.5rem;
	outline: none;

	&:focus {
		outline: none;
	}

	&.shrink {
		${shrinkLabel};
	}
`;

export const SearchButton = styled.button`
	width: 32px;
	height: 31px;

	outline: none;

	background: #f2f1f9;
	background: lightgrey;
	border: none;
	border-bottom: 1px solid grey;
	cursor: pointer;
	&:hover {
		background: lightgrey;
		i {
			opacity: 0.2;
		}
	}
`;
