import styled from "styled-components";

export const SingleProductPageContainer = styled.div`
	margin: auto;
	padding: 50px 20px;
	border: bold 20px slategrey;
	width: 80vw;
	height: 90vh;
	display: flex;
	background-color: lightgreen;
	flex-flow: row nowrap;
	justify-content: space-between;
`;
