import React, { useEffect } from "react";
import { Route } from "react-router-dom";
import SingleProduct from "../../Components/singleProduct/SingleProduct";
import { getSingleProductStart } from "../../actions/productActions";
import { fetchCollectionsStart } from "../../actions/shopActions";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import { selectCollectionsForPreview } from "../../redux/shop/shopSelectors";
import { getSingleObject } from "../../firebase/firebase";
const SingleProductPage = ({
	match,
	getSingleProductStart,
	collections,
	fetchCollectionsStart,
}) => {
	const { itemId } = match.params;

	useEffect(() => {
		fetchCollectionsStart();
		const product = getSingleObject(collections, itemId);
		//getSingleProductStart(itemId);
	}, []);

	return (
		<>
			<h1>Single Product Page</h1>
			<Route
				path={`/product/${itemId}`}
				render={(product) => <SingleProduct product={product} />}
			/>
		</>
	);
};

const mapStateToProps = createStructuredSelector({
	collections: selectCollectionsForPreview,
});

const mapDispatchToProps = (dispatch) => ({
	getSingleProductStart: (itemId) => dispatch(getSingleProductStart(itemId)),
	fetchCollectionsStart: () => dispatch(fetchCollectionsStart()),
});

export default connect(mapStateToProps, mapDispatchToProps)(SingleProductPage);
