import React from "react";

import { connect } from "react-redux";
import {
	clearItemFromCart,
	removeItem,
	addItem,
} from "../../actions/cartActions";
import {
	CheckoutItemContainer,
	ImageContainer,
	Img,
	Name,
	Quantity,
	Arrow,
	Value,
	Price,
	RemoveButton,
} from "./CheckoutItem.styles";
import "./checkoutItem.scss";

const CheckoutItem = ({ item, clearItem, removeItem, addItem }) => {
	const { imageUrl, name, quantity, price } = item;

	return (
		<CheckoutItemContainer>
			<CheckoutItemImageContainer>
				<CheckoutItemImg src={imageUrl} alt="item" />
			</CheckoutItemImageContainer>
			<CheckoutItemName className="name">{name}</CheckoutItemName>
			<CheckoutItemQuantity className="quantity">
				<Arrow onClick={() => removeItem(item)}>&#10094;</Arrow>
				<CheckoutItemValue>{quantity}</CheckoutItemValue>
				<Arrow onClick={() => addItem(item)}>&#10095;</Arrow>
			</CheckoutItemQuantity>
			<span className="price">${price}</span>
			<div className="remove-button" onClick={() => clearItem(item)}>
				<span>&#10006;</span>
			</div>
		</CheckoutItemContainer>
	);
};

const mapDispatchToProps = (dispatch) => ({
	clearItem: (item) => dispatch(clearItemFromCart(item)),
	removeItem: (item) => dispatch(removeItem(item)),
	addItem: (item) => dispatch(addItem(item)),
});

export default connect(null, mapDispatchToProps)(CheckoutItem);
